<?php//FETCH COUPON DETAILS ON UPDATE COUPONglobal $wpdb;if(isset($_REQUEST['coupon_info'])){	$fetch_var=$_REQUEST['coupon_info'];	$ap_coupon_fecthes=$wpdb->get_row("select * from $wpdb->prefix"."apt_coupons WHERE id = $fetch_var ");	$appointment_category_details = $wpdb->get_results( "SELECT * from $wpdb->prefix"."apt_category");}?><form style="margin-bottom: 0;" action="" method="POST" id="ap_update_coupon">	<div class="form-group">				<div class="row cus-reg">			<div class="col-md-12 col-sm-12 col-xs-12 form-group">				<label><?php _e('Coupon Code:',WL_A_P_SYSTEM); ?> </label>				<input type="text" value="<?php echo $ap_coupon_fecthes->coupon_code ?>" class="coupon form-control" name="u_coupon_code" id="u_coupon_code" placeholder="<?php _e("Add Coupon",WL_A_P_SYSTEM); ?>">				<span  class="validation_alert" id="u_coupon_code_alert"><?php _e("This field is required",WL_A_P_SYSTEM ); ?></span>			</div>		</div>		<div class="col-md-12 col-sm-12 col-xs-12 form-group">			<div class="row cus-reg">				<label><?php _e('Service Name:',WL_A_P_SYSTEM); ?> </label>				<select  name="u_service_name[]" multiple id="u_service_name" class="cat1" style="width:100%;" >					<optgroup label="<?php echo "Select Service";?>">						<?php foreach($appointment_category_details as $appointment_category_single_detail){ ?>						<optgroup label="<?php echo $appointment_category_single_detail->name;?>">							<?php $service_table=	$wpdb->prefix."apt_services";							$appointment_details = $wpdb->get_results( "SELECT * from $service_table where category= '$appointment_category_single_detail->id'");  							foreach($appointment_details as $appointment_single_detail){	?>							<option value="<?php echo $appointment_single_detail->id ?>" <?php selected($ap_coupon_fecthes->service_name,$appointment_single_detail->id);?>><?php echo $appointment_single_detail->name ?></option>							<?php } ?>													</optgroup>						<?php } ?>					</select>					<span  class="validation_alert" id="u_service_name_alert"><?php _e("Please select one",WL_A_P_SYSTEM ); ?></span>				</div>			</div>			<div class="row">				<div class="col-md-6 col-sm-12 col-xs-12 form-group">					<div class="row cus-reg">						<label><?php _e('No. of Total Time Used:',WL_A_P_SYSTEM); ?> </label>						<input type="text" value="<?php echo $ap_coupon_fecthes->time_limit ?>" class="a-time form-control" name="u_time_limit" id="u_time_limit">						<span  class="validation_alert" id="u_time_limit_alert"><?php _e("This field is required",WL_A_P_SYSTEM ); ?></span>						<span  class="validation_alert" id="u_time_limit_alert_2"><?php _e("Please Enter Positive Number",WL_A_P_SYSTEM ); ?></span>						<span  class="validation_alert" id="u_time_limit_no_alert"><?php _e("This field required number",WL_A_P_SYSTEM ); ?></span>					</div>				</div>				<div class="col-md-6 col-sm-12 col-xs-12 form-group">					<div class="row cus-reg">						<label><?php _e('No. of Per Person Used:',WL_A_P_SYSTEM); ?></label>						<input type="text" value="<?php echo $ap_coupon_fecthes->per_user_limit ?>" class="a-user form-control" name="u_per_user_limit" id="u_per_user_limit">						<span  class="validation_alert" id="u_per_user_limit_alert"><?php _e("This field is required",WL_A_P_SYSTEM ); ?></span>						<span  class="validation_alert" id="u_per_user_limit_alert_2"><?php _e("Please Enter Positive Number",WL_A_P_SYSTEM ); ?></span>						<span  class="validation_alert" id="u_per_user_limit_no_alert"><?php _e("This field required number",WL_A_P_SYSTEM ); ?></span>					</div>				</div>			</div>			<div class="row">				<div class="col-md-6 col-sm-12 col-xs-12 form-group">					<div class="row cus-reg">						<label><?php _e('Discount:',WL_A_P_SYSTEM); ?></label>						<input type="text" value="<?php echo $ap_coupon_fecthes->discount_type ?>" class="a-discount form-control" name="u_discount_type" id="u_discount_type">						<span  class="validation_alert" id="u_discount_type_alert"><?php _e("This field is required",WL_A_P_SYSTEM ); ?></span> 						<span  class="validation_alert" id="u_discount_type_alert_2"><?php _e("Please Enter Positive Number",WL_A_P_SYSTEM ); ?></span> 					</div>				</div>				<div class="col-md-6 col-sm-12 col-xs-12 form-group">					<div class="row cus-reg">						<label><?php _e('Discount Type:',WL_A_P_SYSTEM); ?></label>						<select  name="u_discount_method" id="u_discount_method" class="cat1">							<option value="0"><?php _e('-- Select Discount Type --',WL_A_P_SYSTEM); ?></option>							<option value="Flat"  <?php if($ap_coupon_fecthes->discount_method=="Flat"){echo "selected='selected'";}?>><?php _e('Flat Discount',WL_A_P_SYSTEM); ?></option>							<option value="Percent" <?php if($ap_coupon_fecthes->discount_method=="Percent"){echo "selected='selected'";}?>><?php _e('Percent Discount',WL_A_P_SYSTEM); ?></option>						</select>						<span  class="validation_alert" id="c_discount_method_alert"><?php _e("Please select one",WL_A_P_SYSTEM ); ?></span>					</div>				</div>			</div>			<div class="cus-reg">				<div class="row">					<div class="col-md-6 col-sm-12 col-xs-12 form-group">						<label><?php _e('Starting Date:',WL_A_P_SYSTEM); ?></label>						<input id="u_coupon_start_date" value="<?php echo $ap_coupon_fecthes->coupon_start_date ?>" name="u_coupon_start_date"  class="form-control cs-date" type="text"/>						<span  class="validation_alert" id="u_coupon_start_date_alert"><?php _e("This field is required",WL_A_P_SYSTEM ); ?></span>					</div>					<div class="col-md-6 col-sm-12 col-xs-12 form-group">						<label><?php _e('Ending Date:',WL_A_P_SYSTEM); ?> </label>						<input id="u_coupon_end_date" value="<?php echo $ap_coupon_fecthes->coupon_end_date ?>" name="u_coupon_end_date"  class="form-control cs-date" type="text"/>						<span  class="validation_alert" id="u_coupon_end_date_alert"><?php _e("This field is required",WL_A_P_SYSTEM ); ?></span>					</div>					<input type="hidden" name="coupon_use_id" id="coupon_use_id" value="<?php echo $ap_coupon_fecthes->id ?>" >				</div>			</div>		</div>	</form>