<?php//SHOW COUPON DETAILS IN DATA TABLEglobal $wpdb;$ap_fecth=$wpdb->get_results("select * from $wpdb->prefix"."apt_coupons");$num_rows=count($ap_fecth);if($num_rows !==0){  foreach($ap_fecth as $value)   {        $id=$value->id;        $coupon_code=$value->coupon_code;        $service_name=$value->service_name;        $ap_fecth_service=$wpdb->get_results("select * from $wpdb->prefix"."apt_services where id='$service_name'");        foreach($ap_fecth_service as $ap_fecth_single_service)        {          $single_service_name= $ap_fecth_single_service->name;        }        $time_limit=$value->time_limit;        $temp_per_user_limit =$value->per_user_limit ;        $per_user_limit = $temp_per_user_limit."&nbsp;Times";        $discount_type=$value->discount_type ;        $discount_method=$value->discount_method ;        $temp_coupon_start_date =$value->coupon_start_date;        $coupon_start_date= date("d-m-Y",strtotime($temp_coupon_start_date));        $temp_coupon_end_date =$value->coupon_end_date ;        $coupon_end_date = date("d-m-Y",strtotime($temp_coupon_end_date));        $results["data"][] =array('<input type="checkbox" name="check[]" id="coupon_check" class="checkbox1" value="'.$id.'"/>',$coupon_code,$single_service_name,$discount_type,$discount_method,$per_user_limit,$time_limit,$coupon_start_date,$coupon_end_date,array('<a style="margin-right: 5px;" href="#update_coupon_model"  data-backdrop="true" data-toggle="modal" data-id="'.$id.'"><i class="fas fa-pen"></i></a> &nbsp <a href="#'.$id.'" class="del_coupon">  <i class="fas fa-trash"></i></a>'));  }}else{  $results["data"][] =array(null,null,null,null,null, __("No Coupon Add",WL_A_P_SYSTEM),null,null,null,null);}if($results != null){        wp_send_json($results); // encode and send response      }    else wp_send_json_error(); // {"success":false}?>