<?php//PRINT ALL PAYMENT DATA require_once '../../../../wp-load.php';header('Content-Type: text/csv');header('Content-Disposition: inline; filename="All Payment List-'.date('Y-m-d-H-i-s').'.csv"');$results = $wpdb->get_results( "SELECT * FROM ".$wpdb->prefix ."apt_payment");echo "Payment Type,Customer, Staff, Service, Appointment Date, Amount,Status\r\n";   if (count($results))  {	foreach($results as $result) {		echo $result->payment_type .",".$result->customer .",".$result->customer_email .",".$result->staff .",".$result->service .",".$result->appointment_date .",".$result->amount ."," .$result->coupon_code_applied ."," .$result->amount_after_discount ."," .$result->status ."\r\n";	}}