<?php//SHOW PAYMENT DETAILS IN DATATABLEglobal $wpdb;$ap_payment=$wpdb->get_results("select * from $wpdb->prefix"."apt_payment");$num_rows=count($ap_payment);if($num_rows !==0){ foreach($ap_payment as $value){  $id=$value->id;  $payment_type=$value->payment_type;  $customer=$value->customer;  $customer_email=$value->customer_email;  $staff=$value->staff;  $service =$value->service ;  $temp_appointment_date=$value->appointment_date ;  $appointment_date = date("d-m-Y",strtotime($temp_appointment_date));  $amount_after_discount=$value->amount_after_discount ;  if($amount_after_discount==""){	   $amount =$value->amount ;  }else{	  $amount =$amount_after_discount ;  }   // $amount =$value->amount ;  $status =$value->status ;  $results["data"][] =array('<input type="checkbox" name="check[]" id="payment_check" class="checkbox_payment" value="'.$id.'"/>',$payment_type,$customer,$customer_email,$staff,$service,$appointment_date,$amount,$status,  '<button type="button" class="btn theme-customer" data-toggle="modal" data-target="#view_payment" data-id="'.$id.'">'.__("Detail", WL_A_P_SYSTEM).'</button>','<a href="#'.$id.'" class="del_payment btn theme-delete"> <i class="fas fa-times-circle"></i></a>');}}else{$results["data"][] =array(null,null,null,null,null,__('No Payment Found',WL_A_P_SYSTEM),null,null,null,null,null);} if($results != null)    {        wp_send_json($results); // encode and send response    }    else wp_send_json_error(); // {"success":false}?>